mapper.select:
SELECT examineResult
FROM user_signup
WHERE username = #{username}

SELECT ui.username, ui.tele, ui.email, ui.name, ui.sfzh, ui.xb, user_signup.majorName, user_signup.majorLevel, user_signup.courseCode, user_signup.file, user_signup..examineResult, FROM user_signup CROSS JOIN user_information ui on user_signup.username = ui.username
majorName = #{majorName}
AND majorLevel = #{majorLevel}
AND examineResult = #{examineResult}
AND xb = #{xb}
AND name LIKE CONCAT('%', #{name}, '%')
AND sfzh LIKE CONCAT('%', #{sfzh}, '%')
AND username LIKE CONCAT('%', #{username}, '%')
AND email LIKE CONCAT('%', #{email}, '%')
AND tele LIKE CONCAT('%', #{tele}, '%')

mapper.update:
UPDATE user_signup
SET examineResult = #{examineResult}
WHERE username = #{username}
